#' Raju & Mantel-Haenszel method
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param group A string naming the grouping variable from \code{data}
#' @param raju .
#' @param mh .
#' @param padjust .
#' @param padjust1 .
#' @param zplot .
#' @param iccplot .
#' @param plot1 .
#' @param gmh .
#' @param fn .
#' @param plot2 .
#' @param padjust2 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$raju} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$mh} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$zplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$gmh} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$iccplot} \tab \tab \tab \tab \tab an array of plots \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$raju$asDF}
#'
#' \code{as.data.frame(results$raju)}
#'
#' @export
dif <- function(
    data,
    vars,
    group,
    raju = FALSE,
    mh = FALSE,
    padjust = "BH",
    padjust1 = "BH",
    zplot = FALSE,
    iccplot = FALSE,
    plot1 = FALSE,
    gmh = FALSE,
    fn = "2,3,4",
    plot2 = FALSE,
    padjust2 = "BH") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("dif requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(group), group, NULL))


    options <- difOptions$new(
        vars = vars,
        group = group,
        raju = raju,
        mh = mh,
        padjust = padjust,
        padjust1 = padjust1,
        zplot = zplot,
        iccplot = iccplot,
        plot1 = plot1,
        gmh = gmh,
        fn = fn,
        plot2 = plot2,
        padjust2 = padjust2)

    analysis <- difClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

