    "facetBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "snowIRT",
                name = "facet",
                version = c(1,0,0),
                options = options,
                results = facetResults$new(options=options),
                data = data,
