                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'auto')
        }))

#' Many Facet Rasch Model
#'
#' 
#' @param data .
#' @param dep .
#' @param id .
#' @param facet .
#' @param rm .
#' @param im .
#' @param inter .
#' @param sm .
#' @param ifit .
#' @param pm .
#' @param pfit .
#' @param plot1 .
#' @param width1 .
#' @param height1 .
#' @param plot2 .
#' @param width2 .
#' @param height2 .
#' @param plot3 .
#' @param width3 .
#' @param height3 .
#' @param angle .
#' @param plot4 .
#' @param width4 .
#' @param height4 .
#' @param plot5 .
#' @param num .
#' @param width5 .
#' @param height5 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text1} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$rm} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$im} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$inter} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$sm} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$ifit} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$pm} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$pfit} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot4} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot5} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$rm$asDF}
#'
#' \code{as.data.frame(results$rm)}
#'
#' @export
facet <- function(
    data,
    dep,
    id,
    facet,
    rm = FALSE,
    im = FALSE,
    inter = FALSE,
    sm = FALSE,
    ifit = FALSE,
    pm = FALSE,
    pfit = FALSE,
    plot1 = FALSE,
    width1 = 500,
    height1 = 500,
    plot2 = FALSE,
    width2 = 500,
    height2 = 500,
    plot3 = FALSE,
    width3 = 500,
    height3 = 500,
    angle = 0,
    plot4 = FALSE,
    width4 = 500,
    height4 = 500,
    plot5 = FALSE,
    num = 1,
    width5 = 500,
    height5 = 500) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("facet requires jmvcore to be installed (restart may be required)")

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(id)) id <- jmvcore::resolveQuo(jmvcore::enquo(id))
    if ( ! missing(facet)) facet <- jmvcore::resolveQuo(jmvcore::enquo(facet))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(id), id, NULL),
            `if`( ! missing(facet), facet, NULL))

    for (v in id) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in facet) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- facetOptions$new(
        dep = dep,
        id = id,
        facet = facet,
        rm = rm,
        im = im,
        inter = inter,
        sm = sm,
        ifit = ifit,
        pm = pm,
        pfit = pfit,
        plot1 = plot1,
        width1 = width1,
        height1 = height1,
        plot2 = plot2,
        width2 = width2,
        height2 = height2,
        plot3 = plot3,
        width3 = width3,
        height3 = height3,
        angle = angle,
        plot4 = plot4,
        width4 = width4,
        height4 = height4,
        plot5 = plot5,
        num = num,
        width5 = width5,
        height5 = height5)

    analysis <- facetClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

