    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "snowIRT",
                name = "polytomous",
                version = c(1,0,0),
                options = options,
                results = polytomousResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'none')
        }))

#' Polytomous Rasch Model
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param imeasure .
#' @param ise .
#' @param infit .
#' @param outfit .
#' @param reliability .
#' @param modelfit .
#' @param modelfitp .
#' @param mat .
#' @param thresh .
#' @param pmeasure .
#' @param thurs .
#' @param wplot .
#' @param inplot .
#' @param outplot .
#' @param angle a number from 0 to 90 defining the angle of the x-axis labels,
#'   where 0 degrees represents completely horizontal labels.
#' @param tau .
#' @param model .
#' @param lr .
#' @param piplot .
#' @param to .
#' @param st .
#' @param plot2 .
#' @param plot3 .
#' @param width .
#' @param height .
#' @param width5 .
#' @param height5 .
#' @param num .
#' @param plot4 .
#' @param width4 .
#' @param height4 .
#' @param num1 .
#' @param plot6 .
#' @param width6 .
#' @param height6 .
#' @param width7 .
#' @param height7 .
#' @param width3 .
#' @param height3 .
#' @param width2 .
#' @param height2 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$ia$items} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$ia$thresh} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$ia$thurs} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$mf$scale} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$mf$mat} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$mcc$model} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$mcc$lr} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$ss$st} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$ss$to} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$wplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$piplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot4} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot6} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$inplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$outplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$total} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$personmeasure} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$pse} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$pinfit} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$poutfit} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$resid} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
polytomous <- function(
    data,
    vars,
    imeasure = TRUE,
    ise = TRUE,
    infit = FALSE,
    outfit = FALSE,
    reliability = TRUE,
    modelfit = FALSE,
    modelfitp = FALSE,
    mat = FALSE,
    thresh = TRUE,
    pmeasure = FALSE,
    thurs = FALSE,
    wplot = TRUE,
    inplot = FALSE,
    outplot = FALSE,
    angle = 0,
    tau = FALSE,
    model = FALSE,
    lr = FALSE,
    piplot = TRUE,
    to = FALSE,
    st = FALSE,
    plot2 = FALSE,
    plot3 = FALSE,
    width = 500,
    height = 500,
    width5 = 500,
    height5 = 500,
    num = 1,
    plot4 = FALSE,
    width4 = 500,
    height4 = 500,
    num1 = 1,
    plot6 = FALSE,
    width6 = 500,
    height6 = 500,
    width7 = 500,
    height7 = 500,
    width3 = 500,
    height3 = 500,
    width2 = 500,
    height2 = 500) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("polytomous requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))


    options <- polytomousOptions$new(
        vars = vars,
        imeasure = imeasure,
        ise = ise,
        infit = infit,
        outfit = outfit,
        reliability = reliability,
        modelfit = modelfit,
        modelfitp = modelfitp,
        mat = mat,
        thresh = thresh,
        pmeasure = pmeasure,
        thurs = thurs,
        wplot = wplot,
        inplot = inplot,
        outplot = outplot,
        angle = angle,
        tau = tau,
        model = model,
        lr = lr,
        piplot = piplot,
        to = to,
        st = st,
        plot2 = plot2,
        plot3 = plot3,
        width = width,
        height = height,
        width5 = width5,
        height5 = height5,
        num = num,
        plot4 = plot4,
        width4 = width4,
        height4 = height4,
        num1 = num1,
        plot6 = plot6,
        width6 = width6,
        height6 = height6,
        width7 = width7,
        height7 = height7,
        width3 = width3,
        height3 = height3,
        width2 = width2,
        height2 = height2)

    analysis <- polytomousClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

