#' Polytomous Rasch Model
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param imeasure .
#' @param ise .
#' @param infit .
#' @param outfit .
#' @param reliability .
#' @param modelfit .
#' @param modelfitp .
#' @param mat .
#' @param thresh .
#' @param pmeasure .
#' @param thurs .
#' @param icc .
#' @param wplot .
#' @param esc .
#' @param inplot .
#' @param outplot .
#' @param angle a number from 0 to 90 defining the angle of the x-axis labels,
#'   where 0 degrees represents completely horizontal labels.
#' @param tau .
#' @param model .
#' @param lr .
#' @param piplot .
#' @param to .
#' @param st .
#' @param plot2 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$st} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$to} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$scale} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$model} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$lr} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$mat} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$items} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$thresh} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$thurs} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$wplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$piplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$esc} \tab \tab \tab \tab \tab an array of plots \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an array of plots \cr
#'   \code{results$inplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$outplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$total} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$personmeasure} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$pse} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$pinfit} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$poutfit} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$resid} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$st$asDF}
#'
#' \code{as.data.frame(results$st)}
#'
#' @export
polytomous <- function(
    data,
    vars,
    imeasure = TRUE,
    ise = TRUE,
    infit = FALSE,
    outfit = FALSE,
    reliability = TRUE,
    modelfit = FALSE,
    modelfitp = FALSE,
    mat = FALSE,
    thresh = TRUE,
    pmeasure = FALSE,
    thurs = FALSE,
    icc = FALSE,
    wplot = TRUE,
    esc = FALSE,
    inplot = FALSE,
    outplot = FALSE,
    angle = 0,
    tau = FALSE,
    model = FALSE,
    lr = FALSE,
    piplot = TRUE,
    to = FALSE,
    st = FALSE,
    plot2 = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("polytomous requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))


    options <- polytomousOptions$new(
        vars = vars,
        imeasure = imeasure,
        ise = ise,
        infit = infit,
        outfit = outfit,
        reliability = reliability,
        modelfit = modelfit,
        modelfitp = modelfitp,
        mat = mat,
        thresh = thresh,
        pmeasure = pmeasure,
        thurs = thurs,
        icc = icc,
        wplot = wplot,
        esc = esc,
        inplot = inplot,
        outplot = outplot,
        angle = angle,
        tau = tau,
        model = model,
        lr = lr,
        piplot = piplot,
        to = to,
        st = st,
        plot2 = plot2)

    analysis <- polytomousClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

